import { projects } from "../data/projects";
import Head from "next/head";

export default function Home() {
  const getTheme = (theme) => {
    return theme;
  };

  return (
    <>
      <Head>
        <title>Naveen DA Portfolio | Home</title>
      </Head>
      <div className="homepage">
        <main>
          <div className="container">
            <div className="row">
              <div className="col">
                <div className="profile-image">
                  <img src="/naveen-da-profile.png" />
                </div>
                <h1>Hi,</h1>
                <h1>I'm Naveen DA</h1>
                <p className="info">
                  I develop an application with high concern about design and
                  UX, sometimes I do an over-engineering process for doing a
                  simple job
                </p>
                <p className="icons">
                  <a href="https://stackoverflow.com/users/6335029/naveenda">
                    <i className="fa fa-stack-overflow" aria-hidden="true"></i>
                  </a>
                  <a href="https://github.com/NaveenDA">
                    <i className="fa fa-github" aria-hidden="true"></i>
                  </a>
                  <a href="https://twitter.com/NaveenDA_">
                    <i className="fa fa-twitter" aria-hidden="true"></i>
                  </a>
                  <a href="https://dribbble.com/naveenda">
                    <i className="fa fa-dribbble" aria-hidden="true"></i>
                  </a>
                  <a href="http://medium.com/@NaveenDA">
                    <i className="fa fa-medium" aria-hidden="true"></i>
                  </a>
                  <a href="https://www.linkedin.com/in/naveenda/">
                    <i className="fa fa-linkedin-square" aria-hidden="true"></i>
                  </a>
                </p>
              </div>

              <div className="col">
                <div className="projects">
                  {projects.map((item) => (

                    <a href={item.repo} className="card" key={item.name}>
                      <div>
                        <div className="name">{item.name}</div>
                        <div className="info">{item.description}</div>
                        {item.tags.map((tag) => (
                          <div key={tag} className="tag">
                            {tag}
                          </div>
                        ))}
                      </div>
                    </a>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </main>

        <style jsx>{`
          .container {
            width: 80%;
            margin: 12px auto;
            margin-bottom: 0;
          }
          .row {
            display: flex;
          }
          .col {
            flex: 0 50%;
          }
          .icons {
            margin: 25px auto;
          }
          .icons a {
            color: #333 !important;
            padding-left: 12px;
            font-size: 18px;
          }
          .info {
            padding: 12px 5px;
            color: #2e302f;
            font-weight: 300;
          }

          .projects {
            display: flex;
            flex-flow: wrap;
            overflow-y: auto;
            height: 92vh;
          }
          .card {
            padding: 1em;
            box-sizing: border-box;
            width: 50%;
            display: flex;
            height: 245px;
            text-decoration: none !important;
          }
          .card > div {
            flex: 1 1 auto;
            padding: 1em;
            border: 1px solid transparent;
            background-color: ${getTheme("cardBg")};
            border-radius: 16px;
            box-shadow: ${getTheme("cardBoxShadow")};
          }
          .card > div:hover {
            background: #ffecd8;
            transition: background-color 100ms linear;
          }
          .card > div:hover .name {
            text-shadow: 0 1px 10px rgba(0, 0, 0, 0.4);
          }
          .card > div:hover .tag {
            background-color: #fa8738;
            color: #fff;
            transition: all 100ms linear;
          }
          .card .name {
            font-size: 18px;
            font-weight: bold;
            color: #000000;
          }
          .card .info {
            color: rgb(46 48 47 / 77%);
          }
          .tag {
            display: inline-block;
            font-size: 12px;
            font-weight: 500;
            line-height: 18px;
            border: 1px solid transparent;
            border-radius: 2em;
            padding: 0 10px;
            line-height: 22px;
            color: #0366d6;
            white-space: nowrap;
            margin: 0 0.125em 0.333em 0;
            background-color: #f1f8ff;
          }

          @media only screen and (max-width: 600px) {
            body {
              overflow-y: auto !important;
            }
            .row {
              flex-wrap: wrap;
            }
            .col {
              flex: 0 100%;
            }
            .projects {
              height: auto !important;
            }
            .card {
              width: 100%;
            }

            .card > div {
              box-shadow:
          /* The top layer shadow */ 0 1px 1px
                  rgba(0, 0, 0, 0.15),
                /* The second layer */ 0 10px 0 -5px #eee,
                /* The second layer shadow */ 0 10px 1px -4px rgba(0, 0, 0, 0.15),
                /* The third layer */ 0 20px 0 -10px #eee,
                /* The third layer shadow */ 0 20px 1px -9px rgba(0, 0, 0, 0.15);
            }
          }
        `}</style>
      </div>
    </>
  );
}
